import React, { useState } from "react";
const base_uri = process.env.REACT_APP_BASE_URL;

export default function ProfilePic({ changeProfile }) {
    const [file, setFile] = useState(null);

    const uploadDP = () => {
        if (!file) return;
        const data = new FormData();
        data.append("photo", file);

        fetch(`${base_uri}/uploadDP`, {
            method: "PUT",
            headers: {
                "Authorization": "Bearer " + localStorage.getItem("jwt")
            },
            body: data,
        })
        .then(res => res.json())
        .then(result => {
            if (result.error) return console.error(result.error);
            localStorage.setItem("user", JSON.stringify(result.user));
            changeProfile();
            window.location.reload();
        })
        .catch(err => console.error(err));
    };

    const removeDP = () => {
        fetch(`${base_uri}/removeDP`, {
            method: "PUT",
            headers: { "Authorization": "Bearer " + localStorage.getItem("jwt") },
        })
        .then(res => res.json())
        .then(result => {
            if (result.error) return console.error(result.error);
            localStorage.setItem("user", JSON.stringify(result.user));
            changeProfile();
            window.location.reload();
        })
        .catch(err => console.error(err));
    };

    return (
        <div className="profile-pic-modal">
            <input type="file" onChange={e => setFile(e.target.files[0])} />
            <button onClick={uploadDP}>Upload</button>
            <button onClick={removeDP}>Remove</button>
            <button onClick={changeProfile}>Close</button>
        </div>
    );
}
