import React, { useEffect, useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { toast } from 'react-toastify'
import "../styles/Home.css"

const base_uri = process.env.REACT_APP_BASE_URL

export default function Home() {
    const navigate = useNavigate()
    const [data, setData] = useState([]) // all posts
    const [comment, setComment] = useState("")
    const [show, setShow] = useState(false) // modal for single post
    const [item, setItem] = useState({}) // selected post

    const picLink = "https://cdn-icons-png.flaticon.com/512/3177/3177440.png"

    // Toast functions
    const notifyA = (msg) => toast.success(msg)
    const notifyB = (msg) => toast.error(msg)

    useEffect(() => {
        const token = localStorage.getItem("jwt")
        if (!token) {
            navigate('/signin')
            return
        }

        fetch(`${base_uri}/allPosts`, {
            method: "get",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token
            }
        })
            .then(res => res.json())
            .then(result => {
                console.log("Fetched posts:", result)
                // Ensure we only set data if it's an array
                setData(Array.isArray(result) ? result : [])
            })
            .catch(err => {
                console.error("Error fetching posts:", err)
                setData([])
            })
    }, [navigate])

    // Toggle modal view for post details
    const toggleComment = (post) => {
        if (show) {
            setShow(false)
            setItem({})
        } else {
            setShow(true)
            setItem(post)
        }
    }

    // Like post
    const likePost = (id) => {
        fetch(`${base_uri}/like`, {
            method: "put",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + localStorage.getItem("jwt")
            },
            body: JSON.stringify({ postId: id })
        })
            .then(res => res.json())
            .then(result => {
                if (!result || !result._id) return
                const newData = data.map(post => post._id === result._id ? result : post)
                setData(newData)
            })
            .catch(err => console.log(err))
    }

    // Unlike post
    const unlikePost = (id) => {
        fetch(`${base_uri}/unlike`, {
            method: "put",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + localStorage.getItem("jwt")
            },
            body: JSON.stringify({ postId: id })
        })
            .then(res => res.json())
            .then(result => {
                if (!result || !result._id) return
                const newData = data.map(post => post._id === result._id ? result : post)
                setData(newData)
            })
            .catch(err => console.log(err))
    }

    // Make a comment
    const makeComment = (text, id) => {
        if (!text) return
        fetch(`${base_uri}/comment`, {
            method: "put",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + localStorage.getItem("jwt")
            },
            body: JSON.stringify({ text, postId: id })
        })
            .then(res => res.json())
            .then(result => {
                if (!result || !result._id) return
                const newData = data.map(post => post._id === result._id ? result : post)
                setData(newData)
                setComment("")
                notifyA("Comment Posted")
            })
            .catch(err => console.log(err))
    }

    return (
        <div className="home">
            {Array.isArray(data) && data.length > 0 ? (
                data.map(posts => (
                    <div className="card" key={posts._id}>
                        {/* Card Header */}
                        <div className="card-header">
                            <div className="card-pic">
                                <img src={posts.postedBy.Photo || picLink} alt="" />
                            </div>
                            <Link to={`/profile/${posts.postedBy._id}`}>
                                <h5>{posts.postedBy.name}</h5>
                            </Link>
                        </div>

                        {/* Card Image */}
                        <div className="card-image">
                            <img src={posts.photo} alt="" />
                        </div>

                        {/* Card Content */}
                        <div className="card-content">
                            {posts.likes.includes(JSON.parse(localStorage.getItem("user"))._id) ? (
                                <span className="material-symbols-outlined material-symbols-outlined-red" onClick={() => unlikePost(posts._id)}>favorite</span>
                            ) : (
                                <span className="material-symbols-outlined" onClick={() => likePost(posts._id)}>favorite</span>
                            )}
                            <p className='likes'>{posts.likes.length} likes</p>
                            <p className='body-text'><span className='body-name'>{posts.postedBy.name}</span> {posts.body}</p>
                            <p style={{ fontWeight: 550, fontSize: 16, cursor: "pointer" }} onClick={() => toggleComment(posts)}>View all comments</p>
                        </div>

                        {/* Add Comment */}
                        <div className="add-comment">
                            <input type="text" placeholder='Add a comment' value={comment} onChange={(e) => setComment(e.target.value)} />
                            <button className="comment" onClick={() => makeComment(comment, posts._id)}>Post</button>
                        </div>
                    </div>
                ))
            ) : (
                <p>No posts to display.</p>
            )}

            {/* Modal / Post Details */}
            {show && item && (
                <div className="showComment">
                    <div className="container">
                        <div className="postPic">
                            <img src={item.photo} alt="" />
                        </div>
                        <div className="details">
                            <div className="card-header" style={{ borderBottom: "1px solid #00000029" }}>
                                <div className="card-pic">
                                    <img src={item.postedBy?.Photo || picLink} alt="" />
                                </div>
                                <h5>{item.postedBy?.name}</h5>
                            </div>

                            <div className="comment-section" style={{ borderBottom: "1px solid #00000029" }}>
                                {Array.isArray(item.comments) && item.comments.map(comment => (
                                    <p className="comm" key={comment._id}>
                                        <span className="commenter" style={{ fontWeight: "bolder" }}>{comment.postedBy.name}</span>
                                        <span className="commentText">{comment.comment}</span>
                                    </p>
                                ))}
                            </div>

                            <div className="card-content">
                                <p>{item.likes.length} likes</p>
                                <p>{item.body}</p>
                            </div>

                            <div className="add-comment">
                                <span className="material-symbols-outlined">mood</span>
                                <input type="text" placeholder='Add a comment' value={comment} onChange={(e) => setComment(e.target.value)} />
                                <button className="comment" onClick={() => { makeComment(comment, item._id); toggleComment() }}>Post</button>
                            </div>
                        </div>
                    </div>

                    <div className="close-comment" onClick={() => toggleComment()}>
                        <span className="material-symbols-outlined material-symbols-outlined-comment">close</span>
                    </div>
                </div>
            )}
        </div>
    )
}
