import React, { useEffect, useState } from 'react';
import PostDetail from './PostDetail';
import ProfilePic from './ProfilePic';
import "../styles/Profile.css";

let base_uri = process.env.REACT_APP_BASE_URL;

export default function Profile({ userIdProp }) {
    const picLink = "https://cdn-icons-png.flaticon.com/512/3177/3177440.png";
    const [show, setShow] = useState(false);
    const [posts, setPosts] = useState([]);
    const [user, setUser] = useState({});
    const [changePic, setChangePic] = useState(false);
    const [isFollow, setIsFollow] = useState(false);
    const [showDM, setShowDM] = useState(false);

    const loggedUser = JSON.parse(localStorage.getItem("user"));
    const userId = userIdProp || loggedUser._id; // if visiting another user, pass userIdProp

    const toggleDetails = (post) => {
        setShow(!show);
        setPosts(post);
    };

    const changeProfile = () => {
        setChangePic(!changePic);
    };

    const followUser = () => {
        fetch(`${base_uri}/follow`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + localStorage.getItem("jwt")
            },
            body: JSON.stringify({ followId: user._id })
        })
        .then(res => res.json())
        .then(result => {
            setIsFollow(true);
            setUser(prev => ({
                ...prev,
                followers: [...prev.followers, loggedUser._id]
            }));
        })
        .catch(err => console.error(err));
    };

    const unfollowUser = () => {
        fetch(`${base_uri}/unfollow`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + localStorage.getItem("jwt")
            },
            body: JSON.stringify({ followId: user._id })
        })
        .then(res => res.json())
        .then(result => {
            setIsFollow(false);
            setUser(prev => ({
                ...prev,
                followers: prev.followers.filter(f => f !== loggedUser._id)
            }));
        })
        .catch(err => console.error(err));
    };

    const toggleDM = () => setShowDM(!showDM);

    // Fetch user profile and posts
    useEffect(() => {
        const token = localStorage.getItem("jwt");
        if (!token) return;

        fetch(`${base_uri}/user/${userId}`, {
            headers: {
                "Authorization": "Bearer " + token
            }
        })
        .then(res => res.json())
        .then(result => {
            setUser(result.user || {});
            setPosts(result.post || []);
            if (result.user.followers.includes(loggedUser._id)) {
                setIsFollow(true);
            } else {
                setIsFollow(false);
            }
        })
        .catch(err => console.error(err));
    }, [userId]);

    return (
        <div className="profile">
            <div className="profile-frame">
                <div className="profile-pic">
                    <img onClick={changeProfile} src={user?.Photo || picLink} alt="" />
                </div>

                <div className="profile-data">
                    <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                        <h1>{user.name}</h1>

                        {/* Follow & DM buttons only for other users */}
                        {user._id && user._id !== loggedUser._id && (
                            <div style={{ display: "flex", gap: "10px" }}>
                                <button className="followBtn"
                                    onClick={() => isFollow ? unfollowUser() : followUser()}>
                                    {isFollow ? "Unfollow" : "Follow"}
                                </button>
                                <button className="messageBtn" onClick={toggleDM}>
                                    Message
                                </button>
                            </div>
                        )}
                    </div>

                    <div className="profile-info" style={{ display: "flex", gap: "15px" }}>
                        <p>{posts.length} posts</p>
                        <p>{user.followers?.length || 0} followers</p>
                        <p>{user.following?.length || 0} following</p>
                    </div>
                </div>
            </div>

            <hr style={{ width: "90%", opacity: "0.8", margin: "25px auto" }} />

            <div className="gallery">
                {posts.map((post) => (
                    <img className='item' key={post._id} src={post.photo} onClick={() => toggleDetails(post)} />
                ))}
            </div>

            {show && <PostDetail item={posts} user={user} toggleDetails={toggleDetails} />}
            {changePic && <ProfilePic changeProfile={changeProfile} />}

            {/* DM modal */}
            {showDM && (
                <div className="dmModal">
                    <h3>Direct Messages</h3>
                    <p>This is a placeholder DM inbox for {user.name}</p>
                    <button onClick={toggleDM}>Close</button>
                </div>
            )}
        </div>
    );
}
