import React, { useEffect, useState } from 'react';
import PostDetail from './PostDetail';
import "../styles/Profile.css";

const base_uri = process.env.REACT_APP_BASE_URL;

export default function Profile() {
  const picLink = "https://cdn-icons-png.flaticon.com/512/3177/3177440.png";

  const [user, setUser] = useState({});
  const [loggedUser, setLoggedUser] = useState({});
  const [posts, setPosts] = useState([]); // All user posts
  const [selectedPost, setSelectedPost] = useState(null); // For modal view
  const [changePic, setChangePic] = useState(false);
  const [profilePicFile, setProfilePicFile] = useState(null);

  // Toggle Post Detail modal
  const toggleDetails = (post) => {
    setSelectedPost(post);
  };

  // Toggle profile picture upload input
  const toggleProfilePic = () => {
    setChangePic(!changePic);
  };

  // Handle follow/unfollow
  const handleFollow = (follow) => {
    const url = follow ? `${base_uri}/follow` : `${base_uri}/unfollow`;
    fetch(url, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      },
      body: JSON.stringify({ followId: user._id })
    })
      .then(res => res.json())
      .then(data => {
        if (data.error) return console.error(data.error);
        setUser(prev => ({
          ...prev,
          followers: follow
            ? [...(prev.followers || []), loggedUser._id]
            : (prev.followers || []).filter(id => id !== loggedUser._id)
        }));
      })
      .catch(err => console.error(err));
  };

  // Handle profile picture upload
  const handleProfilePicUpload = () => {
    if (!profilePicFile) return;

    const formData = new FormData();
    formData.append('file', profilePicFile);

    fetch(`${base_uri}/updateProfilePic`, {
      method: "PUT",
      headers: { "Authorization": "Bearer " + localStorage.getItem("jwt") },
      body: formData
    })
      .then(res => res.json())
      .then(data => {
        if (data.error) return console.error(data.error);
        setUser(prev => ({ ...prev, Photo: data.Photo }));
        setChangePic(false);
      })
      .catch(err => console.error(err));
  };

  // Load user data
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser) return;
    setLoggedUser(storedUser);

    const token = localStorage.getItem("jwt");
    if (!token) return;

    fetch(`${base_uri}/user/${storedUser._id}`, {
      headers: { "Authorization": "Bearer " + token },
    })
      .then(res => res.json())
      .then(result => {
        setUser(result.user || {});
        setPosts(Array.isArray(result.post) ? result.post : []);
      })
      .catch(err => console.error(err));
  }, []);

  const isFollowing = (user.followers || []).includes(loggedUser._id);

  return (
    <div className="profile">
      <div className="profile-frame">
        <div className="profile-pic">
          <img
            onClick={toggleProfilePic}
            src={user?.Photo || picLink}
            alt="Profile"
          />
        </div>

        <div className="profile-data">
          <h1>{user?.name || loggedUser.name}</h1>

          {loggedUser._id !== user._id && (
            <button
              className="followBtn"
              onClick={() => handleFollow(!isFollowing)}
            >
              {isFollowing ? "Unfollow" : "Follow"}
            </button>
          )}

          <div className="profile-info" style={{ display: "flex", gap: "15px" }}>
            <p>{posts.length} posts</p>
            <p>{user?.followers?.length || 0} followers</p>
            <p>{user?.following?.length || 0} following</p>
          </div>

          {user.bio && <p className="profile-bio">{user.bio}</p>}
        </div>
      </div>

      <hr style={{ width: "90%", opacity: "0.8", margin: "25px auto" }} />

      {changePic && (
        <div className="profile-pic-upload">
          <input
            type="file"
            onChange={(e) => setProfilePicFile(e.target.files[0])}
          />
          <button onClick={handleProfilePicUpload}>Upload</button>
        </div>
      )}

      <div className="gallery">
        {Array.isArray(posts) &&
          posts.map(p => (
            <img
              key={p._id}
              src={p.photo}
              alt="Post"
              onClick={() => toggleDetails(p)}
            />
          ))}
      </div>

      {selectedPost && (
        <PostDetail
          item={selectedPost}
          user={user}
          toggleDetails={() => setSelectedPost(null)}
        />
      )}
    </div>
  );
}
