import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import PostDetail from './PostDetail';
import ProfilePic from './ProfilePic';
import "../styles/Profile.css";

let base_uri = process.env.REACT_APP_BASE_URL;

export default function Profile() {
    const picLink = "https://cdn-icons-png.flaticon.com/512/3177/3177440.png";
    const { userid } = useParams(); // for visiting other users
    const [user, setUser] = useState({});
    const [posts, setPosts] = useState([]);
    const [changePic, setChangePic] = useState(false);
    const [showPost, setShowPost] = useState(false);
    const [postItem, setPostItem] = useState({});
    const [isFollow, setIsFollow] = useState(false);
    const [dmOpen, setDmOpen] = useState(false); // Direct Message placeholder

    const loggedUser = JSON.parse(localStorage.getItem("user"));

    // Toggle post details modal
    const toggleDetails = (post = {}) => {
        setShowPost(!showPost);
        setPostItem(post);
    };

    const changeProfile = () => {
        setChangePic(!changePic);
    };

    const openDM = () => {
        setDmOpen(!dmOpen);
    };

    // Follow user
    const followUser = (followId) => {
        fetch(`${base_uri}/follow`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + localStorage.getItem("jwt")
            },
            body: JSON.stringify({ followId })
        })
        .then(res => res.json())
        .then(data => {
            setIsFollow(true);
            // Update followers count in UI
            setUser(prev => ({
                ...prev,
                followers: [...prev.followers, loggedUser._id]
            }));
        })
        .catch(err => console.error(err));
    };

    // Unfollow user
    const unfollowUser = (followId) => {
        fetch(`${base_uri}/unfollow`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + localStorage.getItem("jwt")
            },
            body: JSON.stringify({ followId })
        })
        .then(res => res.json())
        .then(data => {
            setIsFollow(false);
            // Update followers count in UI
            setUser(prev => ({
                ...prev,
                followers: prev.followers.filter(id => id !== loggedUser._id)
            }));
        })
        .catch(err => console.error(err));
    };

    // Fetch user data
    useEffect(() => {
        const userIdToFetch = userid || loggedUser._id;
        fetch(`${base_uri}/user/${userIdToFetch}`, {
            headers: { "Authorization": "Bearer " + localStorage.getItem("jwt") }
        })
        .then(res => res.json())
        .then(result => {
            setUser(result.user);
            setPosts(result.post || []);
            // Check if loggedUser is following this user
            if (result.user.followers.includes(loggedUser._id)) {
                setIsFollow(true);
            }
        })
        .catch(err => console.error(err));
    }, [userid, loggedUser._id]);

    return (
        <div className="profile">
            {/* Profile frame */}
            <div className="profile-frame">
                <div className="profile-pic">
                    <img onClick={changeProfile} src={user?.Photo || picLink} alt="Profile" />
                </div>

                <div className="profile-data">
                    <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                        <h1>{user?.name}</h1>

                        {/* Show Follow/Unfollow button only if visiting other user */}
                        {userid && userid !== loggedUser._id && (
                            <button
                                className="followBtn"
                                onClick={() => isFollow ? unfollowUser(user._id) : followUser(user._id)}
                            >
                                {isFollow ? "Unfollow" : "Follow"}
                            </button>
                        )}

                        {/* DM button for all users */}
                        {userid && userid !== loggedUser._id && (
                            <button className="dmBtn" onClick={openDM}>Message</button>
                        )}
                    </div>

                    {/* Profile stats */}
                    <div className="profile-info" style={{ display: "flex" }}>
                        <p>{posts.length} posts</p>
                        <p>{user?.followers?.length || 0} followers</p>
                        <p>{user?.following?.length || 0} following</p>
                    </div>
                </div>
            </div>

            <hr style={{ width: "90%", opacity: "0.8", margin: "25px auto" }} />

            {/* Gallery */}
            <div className="gallery">
                {posts.map((p) => (
                    <img key={p._id} className="item" src={p.photo} alt="User post" onClick={() => toggleDetails(p)} />
                ))}
            </div>

            {/* Post Detail Modal */}
            {showPost && <PostDetail item={postItem} user={user} toggleDetails={toggleDetails} />}

            {/* Change Profile Pic Modal */}
            {changePic && <ProfilePic changeProfile={changeProfile} />}

            {/* Direct Message Placeholder */}
            {dmOpen && (
                <div className="dmModal">
                    <h3>Direct Message</h3>
                    <p>This is a placeholder for inbox / messaging UI.</p>
                    <button onClick={openDM}>Close</button>
                </div>
            )}
        </div>
    );
}
